import React, { useState, useEffect} from 'react';
import WorkDescription from './WorkDescription';
import base_url from '../api/bootapi';
import axios from 'axios';
import { toast, ToastContainer } from 'react-toastify';

const WorkDescList=()=>{
    // useEffect(()=>{
    //     document.title="Admin List || LMS"
    // }, []);
     //function to call server
    const getAllWorkFromServer=()=>{
        axios.get(`${base_url}/user/workDetails/`).then(
            (response)=>{
                //console.log(response);
                console.log(response.data);
                toast.success("Work has been Loaded",{
                    position:'bottom-center',
                });
                setWorks(response.data);
            },(error)=>{
                console.log(error);
                toast.error("something went wrong");
            }
        );
    };

    //calling loading course function
    useEffect(()=>{
        getAllWorkFromServer();
    },[]);

    const [workDescription, setWorks]=useState([]);

    return (
        <div>
            <ToastContainer />
            <h1>All Admin Details</h1>
            {workDescription.length > 0
                ? workDescription.map((item)=><WorkDescription key={item.workId} WorkDescription={item} />)
            : "No workDescription"}
            
        </div>
    )
}

export default WorkDescList;
