import React, { Component } from "react";
import { Redirect } from "react-router-dom";
import AuthService from "../../authentication/auth.service";
import userService from "../../authentication/user.service";

export default class Profile extends Component {
  constructor(props) {
    super(props);

    this.state = {
      redirect: null,
      userReady: false,
      details : null,
      currentUser: { username: "" }
    };
  }

  componentDidMount() {
    this.getData();
    const currentUser = AuthService.getCurrentUser();
    const details = userService.getPublicContent(currentUser.id);
    const data = details.json
    console.log(details, currentUser);



    if (!currentUser || !details) this.setState({ redirect: "/home" });
    this.setState({ details: data, currentUser: currentUser, userReady: true });

  }

  render() {
    if (this.state.redirect) {
      return <Redirect to={this.state.redirect} />
    }

    const { currentUser } = this.state;
    const { details } = this.state;


    return (
      <div className="container">
        {(this.state.userReady) ?
          <div>
            <header className="jumbotron">
              <h3>
                <strong>{details.fName}</strong> Profile
                <ul>{this.state && this.state.data && this.state.data.map(item => <li>{item.fName}</li>)}</ul>
          </h3>
            </header>
            <p>
              <strong>Token:</strong>{" "}
              {currentUser.accessToken.substring(0, 20)} ...{" "}
              {currentUser.accessToken.substr(currentUser.accessToken.length - 20)}
            </p>
            <p>
              <strong>Id:</strong>{" "}
              {currentUser.id}
            </p>
            <p>
              <strong>Email:</strong>{" "}
              {currentUser.email}
            </p>
            <p>
              <strong>Address:</strong>{" "}
              {currentUser.username}
            </p>
            <strong>Authorities:</strong>
            <ul>
              {currentUser.roles &&
                currentUser.roles.map((role, index) => <li key={index}>{role}</li>)}
            </ul>
          </div> : null}
      </div>
    );
  }
}
