import React from 'react';
import {
    Card,
    CardBody,
    CardTitle,
    CardSubtitle,
    CardText,
    CardFooter,
    Button,
    Container
} from 'reactstrap';

const WorkDesc=({workDescription})=>{
    return(
        <Card className="text-center">
            <CardBody>
                <CardSubtitle className="font-weight-bold">Available Work</CardSubtitle>
                <CardText>Salary : {workDescription.workAmount}</CardText>
                <CardText>Job Description: {workDescription.description}</CardText>
                <CardText>Location: {workDescription.location}</CardText>
                <CardText>Category: {workDescription.category}</CardText>
            </CardBody>
        </Card>
    )
}
export default WorkDesc;
