import React,{useEffect, useState} from 'react';
import { Button, Container, Form, FormGroup, Input } from 'reactstrap';
import axios from 'axios';
import base_url from '../api/bootapi';
import { toast } from 'react-toastify';

const AddAdmin=()=>{
    useEffect(()=>{
        document.title="Register Admin || LMS"
    }, []);

    const [admin, addNewAdmin]=useState({});

    //form handler function
    const handleForm=(e)=>{
        console.log(admin);
        postDatatoServer(admin);
        e.preventDefault();
    };

    //creating function to post data on server
    const postDatatoServer=(data)=>{
        axios.post(`${base_url}/admin/register/`,data).then(
            (response)=>{
                console.log(response);
                console.log("success");
                toast.success("Admin Added Successfully");
            },(error)=>{
                console.log(error);
                console.log("error");
                toast.error("Admin Registartion Failed");
            }
        );
    };

    return (
        <div>
            <h1 className="text-center my-3">Admin Registration</h1>
            <Form onSubmit={handleForm}>
                <FormGroup>
                    <label for="fName">Enter First Name</label>
                    <Input type="text" placeholder="First Name" name="fName" id="fName" 
                    onChange={(e)=>{
                       addNewAdmin({...admin, fName:e.target.value}); 
                    }} />
                </FormGroup>
                <FormGroup>
                    <label for="lName">Enter Last Name</label>
                    <Input type="text" placeholder="Last Name" name="lName" id="lName"  onChange={(e)=>{
                       addNewAdmin({...admin, lName:e.target.value}); 
                    }} />
                </FormGroup>
                <FormGroup>
                    <label for="dob">Enter Date of Birth</label>
                    <Input type="date" placeholder="DOB" name="dob" id="dob"  onChange={(e)=>{
                       addNewAdmin({...admin, dob:e.target.value}); 
                    }}/>
                </FormGroup>
                <FormGroup>
                    <label for="gender">Enter your Gender</label>
                    <Input type="text" placeholder="Gender" name="gender" id="gender"  onChange={(e)=>{
                       addNewAdmin({...admin, gender:e.target.value}); 
                    }}/>
                </FormGroup>
                <FormGroup>
                    <label for="email">Enter Your Email</label>
                    <Input type="text" placeholder="Email" name="email" id="email"  onChange={(e)=>{
                       addNewAdmin({...admin, email:e.target.value}); 
                    }}/>
                </FormGroup>
                <FormGroup>
                    <label for="password">Enter Your Password</label>
                    <Input type="text" placeholder="Password" name="password" id="password"  onChange={(e)=>{
                       addNewAdmin({...admin, password:e.target.value}); 
                    }}/>
                </FormGroup>
                <FormGroup>
                    <label for="phoneNo">Enter Your Phone Number</label>
                    <Input type="text" placeholder="Phone No" name="phoneNo" id="phoneNo"  onChange={(e)=>{
                       addNewAdmin({...admin, phoneNo:e.target.value}); 
                    }}/>
                </FormGroup>
                <Container className="text-center">
                    <Button color="success" type="submit">Register Admin</Button>
                    <Button color="warning ml-3" type="reset">Clear</Button>
                </Container>
            </Form>
        </div>
    )
}
export default AddAdmin;
