import React, { useEffect, useState } from 'react';
import { Button, Container, Form, FormGroup, Input } from 'reactstrap';
import axios from 'axios';
import base_url from '../../api/bootapi';
import { toast, ToastContainer } from 'react-toastify';
import { Link, useHistory } from 'react-router-dom';
import { Dropdown, DropdownButton } from 'react-bootstrap';

const Login = () => {

  const wrapper = {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    width: '100%',
    padding: '50px',
    color: '#444',
    border: '1px solid #1890ff',
  };

  useEffect(() => {
    document.title = "Login User || LMS"
  }, []);

  let history = useHistory();
  const redirect = (userType,userId) => {
    var url=`/profile/${userType}/${userId}`;
    setTimeout(() => {
      history.push(url);
    }, 2500)
  }

  var em = useState({});
  var pass = useState({});

  //form handler function
  const handleForm = (e) => {
    postDatatoServer();
    e.preventDefault();
  };

  //creating function to post data on server
  const postDatatoServer = () => {
    axios.post(`${base_url}/user/login/${em}/${pass}`).then(
      (response) => {
        console.log(response);
        console.log("success");
        toast.success("Login Successful");
        redirect(response.data.userType,response.data.userId);
      }, (error) => {
        console.log(error);
        console.log("error");
        toast.error("Incorrect Email or Password");
      }
    );
  };

  return (
    <div style={wrapper}>
      <ToastContainer />
      <Container>
        <h1 className="text-center my-3">Login Form</h1>
        <Form onSubmit={handleForm}>
          <FormGroup>
            <label for="email">Enter Email</label>
            <Input required type="text" placeholder="Email" name="email" id="email"
              onChange={(e) => {
                em = e.target.value;
              }} />
          </FormGroup>
          <FormGroup>
            <label for="pasword">Enter Password</label>
            <Input required type="password" placeholder="Password" name="password" id="password" onChange={(e) => {
              pass = e.target.value;
            }} />
          </FormGroup>
          <Container className="text-center">
            <Button color="success" type="submit">Login</Button>
            <DropdownButton id="dropdown-basic-button" title="Register Here">
              <Link tag="a" to="/add-customer" action><Dropdown.Item href="#/action-1">Register as Customer</Dropdown.Item></Link>
              <Link tag="a" to="/add-worker" action><Dropdown.Item href="#/action-1">Register as Customer</Dropdown.Item></Link>
            </DropdownButton>
          </Container>
        </Form>
      </Container>
    </div>
  )
}
export default Login;
